import{_ as c,r as s,o as l,c as i,a as t,b as e,d as o,w as r,e as d}from"./app-00f3c486.js";const p={},h=d('<h1 id="腾讯云代码分析介绍" tabindex="-1"><a class="header-anchor" href="#腾讯云代码分析介绍" aria-hidden="true">#</a> 腾讯云代码分析介绍</h1><p><strong>腾讯云代码分析</strong>（Tencent Cloud Code Analysis，简称TCA，内部曾用研发代号 CodeDog ）是集众多分析工具的云原生、分布式、高性能的代码综合分析跟踪平台，包含<strong>服务端、Web端、客户端</strong>三个组件，已集成一批自研工具，同时也支持动态集成业界各编程语言的分析工具。</p><p><strong>代码分析</strong>是通过词法分析、语法分析、控制流、数据流分析等技术对程序代码进行扫描，对代码进行综合分析，验证代码是否满足规范性、安全性、可靠性、可维护性等指标的一种代码分析技术。</p><p>使用TCA可以帮助团队用代码分析技术查找代码中的规范性、结构性、安全漏洞等问题，持续监控项目代码质量并进行告警。同时TCA开放API，支持与上下游系统对接，从而集成代码分析能力，为代码质量提供保障，更有益于传承优良的团队代码文化。</p><p><img src="https://tencent.github.io/CodeAnalysis/media/Components.png" alt="组件图"></p><p><img src="https://tencent.github.io/CodeAnalysis/media/Flow.png" alt="流程图"></p><hr>',7),_={href:"https://github.com/Tencent/CodeAnalysis",target:"_blank",rel:"noopener noreferrer"},u=t("p",null,"如您更多环境需求，也可通过以下两种方式部署腾讯云代码分析平台：",-1);function m(g,k){const a=s("ExternalLinkIcon"),n=s("RouterLink");return l(),i("div",null,[h,t("p",null,[e("拉取 TCA "),t("a",_,[e("代码库"),o(a)]),e(" 后，首次体验推荐您使用"),o(n,{to:"/zh/quickStarted/dockerDeploy.html"},{default:r(()=>[e(" Docker 快速部署")]),_:1}),e("方式快速搭建和体验腾讯云代码分析平台。")]),u,t("ul",null,[t("li",null,[o(n,{to:"/zh/quickStarted/dockercomposeDeploy.html"},{default:r(()=>[e("通过 Docker-Compose 部署")]),_:1})]),t("li",null,[o(n,{to:"/zh/quickStarted/codeDeploy.html"},{default:r(()=>[e("通过源代码部署")]),_:1})])])])}const f=c(p,[["render",m],["__file","index.html.vue"]]);export{f as default};
